import { createReducer, on, createFeatureSelector, createSelector } from '@ngrx/store';
import * as UserActions from './user.actions';
const initialState = {
  maskUserName: true,
  currentUser: null
};
const getUserFeatureState = createFeatureSelector('users');
export const getMaskUserName = createSelector(getUserFeatureState, state => state.maskUserName);
export const getCurrentUser = createSelector(getUserFeatureState, state => state.currentUser);
export const userReducer = createReducer(initialState, on(UserActions.maskUserName, state => {
  return Object.assign(Object.assign({}, state), {
    maskUserName: !state.maskUserName
  });
}));
